const dotenv = require('dotenv');
import authorizeRequest from '../../../ignite/auth/authentication';
dotenv.config();


// for local development, this file will be replace by auth-prod.js on heroku builds
module.exports = {
    authorizeRequest : function(req, res, next){
        if(process.env.SkipAuthLocalOnly === 'true')
        {
            next();
        }
        else
        {
            return authorizeRequest.authorizeRequest(req, res, next);
        }
    }
}